import pytest

# Define two base classes with different markers
@pytest.mark.foo
class Foo:
    pass

@pytest.mark.bar
class Bar:
    pass

# Derived class inheriting from both base classes
class TestDings(Foo, Bar):
    def test_dings(self):
        pass

def test_dings_markers():
    # Retrieve the markers on the TestDings class
    markers = [mark.name for mark in TestDings.test_dings.pytestmark]

    # Assert that only one marker is present due to MRO
    # BUG: this behavior is incorrect but currently happens due to the bug
    assert len(markers) == 1, "Expected only one marker due to MRO, exposing the bug"

    # Assert that the marker is either 'foo' or 'bar'
    assert markers[0] in ['foo', 'bar'], "Marker should be either 'foo' or 'bar'"
