from typing import Dict, Tuple
from urllib.parse import unquote, urlparse
from requests.exceptions import HTTPError
from sphinx.builders import Builder
from sphinx.locale import __
from sphinx.util import encode_uri, requests

class CheckExternalLinksBuilder(Builder):
    ...

    def check_thread(self) -> None:
        kwargs = {}
        if self.app.config.linkcheck_timeout:
            kwargs['timeout'] = self.app.config.linkcheck_timeout

        def get_request_headers() -> Dict:
            url = urlparse(uri)
            candidates = ['%s://%s' % (url.scheme, url.netloc), '%s://%s/' % (url.scheme, url.netloc), uri, '*']
            for u in candidates:
                if u in self.config.linkcheck_request_headers:
                    headers = dict(DEFAULT_REQUEST_HEADERS)
                    headers.update(self.config.linkcheck_request_headers[u])
                    return headers
            return {}

        def check_uri() -> Tuple[str, str, int]:
            if '#' in uri:
                req_url, anchor = uri.split('#', 1)
                for rex in self.anchors_ignore:
                    if rex.match(anchor):
                        anchor = None
                        break
            else:
                req_url = uri
                anchor = None
            try:
                req_url.encode('ascii')
            except UnicodeError:
                req_url = encode_uri(req_url)
            for pattern, auth_info in self.auth:
                if pattern.match(uri):
                    break
            else:
                auth_info = None
            kwargs['headers'] = get_request_headers()
            try:
                if anchor and self.app.config.linkcheck_anchors:
                    response = requests.get(req_url, stream=True, config=self.app.config, auth=auth_info, **kwargs)
                    response.raise_for_status()
                    found = check_anchor(response, unquote(anchor))
                    if not found:
                        raise Exception(__("Anchor '%s' not found") % anchor)
                else:
                    try:
                        response = requests.head(req_url, allow_redirects=True, config=self.app.config, auth=auth_info, **kwargs)
                        response.raise_for_status()
                    except HTTPError:
                        response = requests.get(req_url, stream=True, config=self.app.config, auth=auth_info, **kwargs)
                        response.raise_for_status()
            except HTTPError as err:
                if err.response.status_code == 401:
                    return ('working', ' - unauthorized', 0)
                elif err.response.status_code == 503:
                    return ('ignored', str(err), 0)
                else:
                    return ('broken', str(err), 0)
            except Exception as err:
                return ('broken', str(err), 0)
            if response.url.rstrip('/') == req_url.rstrip('/'):
                return ('working', '', 0)
            else:
                new_url = response.url
                if anchor:
                    new_url += '#' + anchor
                if response.history:
                    code = response.history[-1].status_code
                    return ('redirected', new_url, code)
                else:
                    return ('redirected', new_url, 0)

        def check(docname: str) -> Tuple[str, str, int]:
            if len(uri) == 0 or uri.startswith(('#', 'mailto:', 'tel:')):
                return ('unchecked', '', 0)
            elif not uri.startswith(('http:', 'https:')):
                if uri_re.match(uri):
                    return ('unchecked', '', 0)
                else:
                    srcdir = path.dirname(self.env.doc2path(docname))
                    if path.exists(path.join(srcdir, uri)):
                        return ('working', '', 0)
                    else:
                        for rex in self.to_ignore:
                            if rex.match(uri):
                                return ('ignored', '', 0)
                        else:
                            self.broken[uri] = ''
                            return ('broken', '', 0)
            elif uri in self.good:
                return ('working', 'old', 0)
            elif uri in self.broken:
                return ('broken', self.broken[uri], 0)
            elif uri in self.redirected:
                return ('redirected', self.redirected[uri][0], self.redirected[uri][1])
            for rex in self.to_ignore:
                if rex.match(uri):
                    return ('ignored', '', 0)
            for _ in range(self.app.config.linkcheck_retries):
                status, info, code = check_uri()
                if status != 'broken':
                    break
            if status == 'working':
                self.good.add(uri)
            elif status == 'broken':
                self.broken[uri] = info
            elif status == 'redirected':
                self.redirected[uri] = (info, code)
            return (status, info, code)
        while True:
            uri, docname, lineno = self.wqueue.get()
            if uri is None:
                break
            status, info, code = check(docname)
            self.rqueue.put((uri, docname, lineno, status, info, code))
