                if not isinstance(p, Rational):
                    raise TypeError('invalid input: %s' % p)
            q = 1
            gcd = 1
        if not isinstance(p, SYMPY_INTS):
            p = Rational(p)
            q *= p.q
            p = p.p
        else:
            p = int(p)
        if not isinstance(q, SYMPY_INTS):
            q = Rational(q)
            p *= q.q
            q = q.p
        else:
            q = int(q)
        if q == 0:
            if p == 0:
                if _errdict['divide']:
                    raise ValueError('Indeterminate 0/0')
                else:
                    return S.NaN
            return S.ComplexInfinity
        if q < 0:
            q = -q
            p = -p
        if not gcd:
            gcd = igcd(abs(p), q)
        if gcd > 1:
            p //= gcd
            q //= gcd
        if q == 1:
            return Integer(p)
        if p == 1 and q == 2:
            return S.Half
        obj = Expr.__new__(cls)
        obj.p = p
        obj.q = q
        return obj

